\psellipse*[linewidth=2.pt,fillcolor=black,fillstyle=solid,opacity=1](-1.7504329007576889,0.24956709924231116)(0.07,0.07)
\parametricplot{2.875340604438868}{3.407844702740719}{2*cos(t)+9.|2*sin(t)+0.}
