\rput[tl](1.9008979784859343,5.797234712319029){$71^\circ$}
\begin{scriptsize}
\psdots[dotsize=3pt 0,dotstyle=*,linecolor=ttqqqq](1.,5.)
\psdots[dotsize=3pt 0,dotstyle=*,linecolor=ttqqqq](1.,1.)
