\rput[bl](-1.7,-1.2){\normalsize \red{$S_1$}}
\psdots[dotsize=6pt 0,dotstyle=*,linecolor=red](-1,-1)
\rput[bl](1.3,-1.2){\normalsize \red{$S_2$}}
\psdots[dotsize=6pt 0,dotstyle=*,linecolor=red](1,-1)
